def model_evaluator(pred_func, datasets: tuple):
    """input a prediction function and a tuple of 2 datasets"""
    pred_func = np.vectorize(pred_func)
    dataset_1, dataset_2 = datasets
    return (sum(pred_func(dataset_1)), sum(pred_func(dataset_2)))

from keras.preprocessing import image                  
from tqdm import tqdm

def path_to_tensor(img_path):
    # loads RGB image as PIL.Image.Image type
    img = image.load_img(img_path, target_size=(224, 224))
    # convert PIL.Image.Image type to 3D tensor with shape (224, 224, 3)
    x = image.img_to_array(img)
    # convert 3D tensor to 4D tensor with shape (1, 224, 224, 3) and return 4D tensor
    return np.expand_dims(x, axis=0)

def paths_to_tensor(img_paths):
    list_of_tensors = [path_to_tensor(img_path) for img_path in tqdm(img_paths)]
    return np.vstack(list_of_tensors)

